package org.tnsif.typecasting;

public class TpecastingDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//implicit(convert smallest to largest)
			int x=15;
			float y=x;
			System.out.println("The value of Y is: "+y);
			
			//Explicit(convert largets to smallest)
			double d=12.5;
			long l=(long)d;
			System.out.println("The value of 1 is: "+l);
		}

	}

